import React, { useState } from 'react'; 
import { Tooltip } from "react-tooltip";
import "react-tooltip/dist/react-tooltip.css";
import useGlobalTaskUpdate from './useGlobalTaskUpdate';

const DayCard = ({ date, data }) => {      

  const divStyles = {
      backgroundColor: 'white',
      border: '2px solid black',
      width: 'calc(14.28571428% - 4px)',
      aspectRatio: '1 / 0.5',
      margin: '0px',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',      
  };

  const tasksDivStyles = {
    backgroundColor: 'lightgrey',
    width: '100%',
    height: '10%',
    display: 'flex',    
    justifyContent: 'flex-end',
    marginTop: 'auto'
  };

  const taskDivStyles = {
    backgroundColor: 'grey',
    width: '20px',
    height: '100%',
    marginLeft: '2px',    
    cursor: 'pointer'
  };

  const {updateStatus, loading} = useGlobalTaskUpdate();

  const formattedDate = date
    ? date.toLocaleDateString('es-ES', { weekday: 'long', day: 'numeric' })
      .replace(/^\w/, (char) => char.toUpperCase())
      .replace(/(?:^|\s)\w/g, (char) => char.toUpperCase())
    : 'No date provided';
  
  const currentDateOnly = new Date(date);
  currentDateOnly.setHours(0, 0, 0, 0); 
  
  const normalizeTaskDate = (taskDate) => {
    const mysqlDate = new Date(taskDate); 
    mysqlDate.setHours(0, 0, 0, 0); 
    return mysqlDate;
  };
  
  const dayTasks = data?.tasks?.filter(task => {
    const taskDateNormalized = normalizeTaskDate(task.globalTaskDate);    
    return currentDateOnly.getTime() === taskDateNormalized.getTime();
  });

  const handleDayClick = () => {

  };

  const handleTaskClick = (id, date, status) => {
    updateStatus({globalTaskId: id, globalTaskDate: date, globalTaskStatus: status});
  };

  return (
    <div style={divStyles}>

      <h3>{formattedDate}</h3>      

      <div style={tasksDivStyles}>
        {dayTasks.map((task) => (
          <div 
            key={task.globalTaskId} 
            style={taskDivStyles} 
            data-tooltip-id={`task-tooltip-${task.globalTaskId}`}
            data-tooltip-content={task.globalTaskName}
            onClick={() => handleTaskClick(task.globalTaskId, task.globalTaskDate, task.globalTaskStatus)}>
            
            <Tooltip id={`task-tooltip-${task.globalTaskId}`}/>
          </div>
        ))}
      </div>      

    </div>    
  );
};

export default DayCard;
