import './App.css';
import Header from './components/header';
import Calendar from './components/calendar';
import Day from './components/day';
import Month from './components/month';
import Task from './components/task';
import useFetch from "./components/useFetch";
import React, { useState, useEffect } from 'react';

function App() {  

  const { data, error, loading } = useFetch();
  const [comp, setComp] = useState(<Day date={new Date()} data={data}/>);

  const handleComponentChange = (component) => {
    setComp(component);    
  };

  if (error) {
    return (
      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <h1>Error</h1>
        <span>{error}</span>
      </div>
    );
  }

  // Optionally, show a loading state before the actual data is available
  if (loading) {
    return (
      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <h1>Loading...</h1>
      </div>
    );
  }

  useEffect(()=>{},[data]);

  return (
    <div className="App">
      <Header handleComponentChange={handleComponentChange}/>
      {React.cloneElement(comp, { handleComponentChange })}
    </div>
  );
}

export default App;
