import React from 'react';
import Task from './task';
import useFetch from "./useFetch";
import { BsPerson, BsPersonSlash } from 'react-icons/bs';

const Day = ({date, data}) => {    
  const headerStyles = {
      backgroundColor: '#282c34',
      minHeight: '10vh',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      color: 'white',
  };    

  const formattedDate = date
    ? date.toLocaleDateString('es-ES', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })
        .replace(/^\w/, (char) => char.toUpperCase())
        .replace(/(?:^|\s)\w/g, (char) => char.toUpperCase())
    : 'No date provided';

  
  const currentDateOnly = new Date(date);
  currentDateOnly.setHours(0, 0, 0, 0); // Normalize by setting time to 00:00:00

  // Function to normalize the MySQL task date (set to midnight)
  const normalizeTaskDate = (taskDate) => {
    const mysqlDate = new Date(taskDate); // Convert to Date object
    mysqlDate.setHours(0, 0, 0, 0); // Normalize by setting time to 00:00:00 (local time)
    return mysqlDate;
  };

  // Filter tasks that match the provided date
  const dayTasks = data?.tasks?.filter(task => {
    const taskDateNormalized = normalizeTaskDate(task.taskDate);
    return currentDateOnly.getTime() === taskDateNormalized.getTime();
  });

  return (
    <div>
      <h1>{formattedDate}</h1>    
      {dayTasks && dayTasks.length > 0 ? (
        <div>
          <h2>Tasks for today:</h2>
          <ul>
            {dayTasks.map((task) => (
              <li key={task.taskId}>
                <span>{task}</span>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>No tasks for today</p>
      )}
    </div>    
  );
};

export default Day;
