import React, { useState } from "react";
import useCheckMonth from "./useCheckMonth";
import Day from "./day";
import DayCard from "./dayCard";
import NewGlobalTaskForm from './newGlobalTaskForm'; 
import moment from "moment";

const Month = () => {
  const { tasks, loading, error, currentMonth, setCurrentMonth, checkAndCreateTasks } = useCheckMonth();
  const [showGlobalTaskForm, setShowGlobalTaskForm] = useState(false); // State to toggle form visibility  

  const handleCheckTasks = async () => {
    const [year, month] = currentMonth.split("-");
    await checkAndCreateTasks(year, month);
  };

  const toggleGlobalTaskForm = () => {
    setShowGlobalTaskForm(!showGlobalTaskForm); // Toggle the form visibility
  };

  const daysInMonth = moment(currentMonth, "YYYY-MM").daysInMonth();
  const daysArray = Array.from({ length: daysInMonth }, (_, i) =>
    moment(currentMonth, "YYYY-MM").date(i + 1).toDate()
  );
  const emptySpacesStart = (daysArray[0].getDay() + 6) % 7;
  const emptySpacesEnd = (7- daysArray[daysArray.length - 1].getDay()) % 7;

  const mainDivStyles = {
    border: '2px solid blue',    
    display: 'flex',
    flexWrap: 'wrap',
    alignItems: 'center',
    justifyContent: 'center'
  };

  const daysDivStyles = {
    border: '2px solid grey',
    width: '90%',
    display: 'flex',
    flexWrap: 'wrap',    
  };

  const emptyDivStyles = {
    backgroundColor: 'lightgrey',
    border: '2px solid grey',
    width: 'calc(14.28571428% - 4px)',
    aspectRatio: '1 / 0.5',
    margin: '0px',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',      
  };

  return (
    <div style={mainDivStyles}>
      <h1>{moment(currentMonth, "YYYY-MM").format("MMMM YYYY")}</h1>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      <button onClick={handleCheckTasks}>Check and Create Tasks</button>
      
      <button onClick={toggleGlobalTaskForm}>
        {showGlobalTaskForm ? "Cancelar" : "Nueva Tarea Global"}
      </button>

      {showGlobalTaskForm && <NewGlobalTaskForm />}
      <div style={daysDivStyles}>
        {Array.from({ length: emptySpacesStart }).map((_, index) => (
          <div key={`empty-start-${index}`} style={emptyDivStyles} />
        ))}
        {daysArray.map((day) => (
          <DayCard key={day} date={day} data={{ tasks }} />
        ))}
        {Array.from({ length: emptySpacesEnd }).map((_, index) => (
          <div key={`empty-end-${index}`} style={emptyDivStyles} />
        ))}
      </div>
    </div>
  );
};

export default Month;
